CREATE TABLE `ims_wxz_panorama_win` ( `id` int(11) NOT NULL AUTO_INCREMENT, `uniacid` int(11) unsigned NOT NULL, `fans_id` int(11) NOT NULL COMMENT '粉丝id', `pano` int(11) NOT NULL COMMENT '场景id', `award_id` int(11) NOT NULL COMMENT '奖品id', `award` varchar(50) NOT NULL DEFAULT '' COMMENT '奖品名称', `fee` int(10) NOT NULL COMMENT '红包金额', `is_send` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否发放', `create_time` int(10) NOT NULL COMMENT '中奖时间', PRIMARY KEY (`id`), KEY `fans_id` (`fans_id`), KEY `uniacid` (`uniacid`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE `ims_wxz_panorama_fans` ( `uid` int(11) NOT NULL AUTO_INCREMENT, `uniacid` int(11) unsigned NOT NULL, `openid` varchar(40) NOT NULL DEFAULT '', `proxy` varchar(40) NOT NULL DEFAULT '', `unionid` varchar(40) NOT NULL DEFAULT '', `nickname` varchar(20) NOT NULL DEFAULT '', `gender` varchar(2) DEFAULT '', `state` varchar(20) NOT NULL DEFAULT '', `city` varchar(20) NOT NULL DEFAULT '', `country` varchar(20) NOT NULL DEFAULT '', `award_name` varchar(50) NOT NULL DEFAULT '' COMMENT '奖品名称', `avatar` varchar(500) NOT NULL DEFAULT '', `username` varchar(255) NOT NULL COMMENT '用户姓名', `cellphone` varchar(20) NOT NULL COMMENT '手机号', `award_num` int(11) NOT NULL DEFAULT 0 COMMENT '领取次数', `share_num` int(11) NOT NULL DEFAULT 0 COMMENT '分享次数', `status` tinyint(4) NOT NULL DEFAULT '0', PRIMARY KEY (`uid`), KEY `uniacid` (`uniacid`), KEY `openid` (`openid`), KEY `proxy` (`proxy`), KEY `nickname` (`nickname`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE `ims_wxz_panorama_payrecord` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `uniacid` int(10) unsigned NOT NULL COMMENT '微信', `uid` int(10) unsigned NOT NULL COMMENT '用户ID', `openid` varchar(200) NOT NULL DEFAULT '' COMMENT 'openid', `pano` int(11) NOT NULL COMMENT '场景id', `fee` int(10) NOT NULL COMMENT '发放金额', `log` varchar(500) NOT NULL DEFAULT '' COMMENT '日志', `created` int(10) unsigned NOT NULL COMMENT '创建时间', `completed` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '完成状态,0未完成,1已完成', `pay_time` int(10) unsigned NOT NULL COMMENT '支付时间', KEY `id` (`id`), KEY `uid` (`uid`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='发红包记录表';
CREATE TABLE `ims_wxz_panorama_award` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `uniacid` int(10) unsigned NOT NULL COMMENT '', `type` tinyint(3) NOT NULL DEFAULT '1' COMMENT '奖品类型1:现金 2实物', `name` varchar(200) NOT NULL DEFAULT '' COMMENT '奖品名称', `min_money` int(11) NOT NULL DEFAULT '0' COMMENT '现金最小金额', `max_money` int(11) NOT NULL DEFAULT '0' COMMENT '现金最大金额', `num` int(11) NOT NULL COMMENT '奖品数量', `left_num` int(11) NOT NULL COMMENT '奖品剩余数量', `cashed` int(11) NOT NULL COMMENT '已中奖品数量', `probability` int(11) NOT NULL COMMENT '中奖概率', `create_time` int(10) unsigned NOT NULL COMMENT '创建时间', KEY `id` (`id`), KEY `uniacid` (`uniacid`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='奖品表';
CREATE TABLE `ims_wxz_panorama_scene` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `uniacid` int(10) unsigned NOT NULL COMMENT '', `name` varchar(50) NOT NULL DEFAULT '' COMMENT '全景名称', `up` varchar(255) NOT NULL DEFAULT '' COMMENT '图片1', `back` varchar(255) NOT NULL DEFAULT '' COMMENT '图片2', `down` varchar(255) NOT NULL DEFAULT '' COMMENT '图片3', `front` varchar(255) NOT NULL DEFAULT '' COMMENT '图片4', `left` varchar(255) NOT NULL DEFAULT '' COMMENT '图片5', `right` varchar(255) NOT NULL DEFAULT '' COMMENT '图片6', `audio` varchar(255) NOT NULL DEFAULT '' COMMENT '音频文件', `treasures` varchar(200) NOT NULL DEFAULT '' COMMENT '宝藏图片', `create_time` int(10) unsigned NOT NULL COMMENT '创建时间', KEY `id` (`id`), KEY `uniacid` (`uniacid`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='全景场景表';
CREATE TABLE IF NOT EXISTS `ims_wxz_panorama_page` ( `id` INT(11) NOT NULL AUTO_INCREMENT, `uniacid` INT(11) NOT NULL, `type` VARCHAR(50) NOT NULL COMMENT '页面类型', `title` VARCHAR(500) NOT NULL COMMENT '标题', `desc` TEXT NOT NULL COMMENT '描述', `img` VARCHAR(255) NOT NULL COMMENT '图片', `link` VARCHAR(255) NOT NULL COMMENT '链接地址', `isdel` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否删除', `update_at` INT(11) NOT NULL, `create_at` INT(11) NOT NULL, PRIMARY KEY (`id`) ) ENGINE=INNODB DEFAULT CHARSET=utf8;

